import {loginAction} from '../modules/user'
import axios from 'axios'
import React, {useEffect, useState} from 'react'
import {useSelector, useDispatch} from 'react-redux'
import {Form, Container, Button, Row, Col, Table} from 'react-bootstrap'
import { CategoryDirection } from './CategoryBanner'
import BasicPagination from './BasicPagination'
import '../App.css'
import Modal from './Modal'

export default function PostDetail({history, match}){
    let pageNumber = match.params.number;
    const [ modalOpen, setModalOpen ] = useState(false);
    const closeModal = () => {
        setModalOpen(false);
    }
    const [ modalContents, setModalContents ] = useState('');


    const [post, setPost] = useState([]);
    // const {username, password} = userData;

    const fetchPost = async()=>{
        let res = await axios.get(`/apis/v1/post/${pageNumber}`);
        let data = res.data.payload
        setPost(data);

    }


    useEffect(()=>{
        fetchPost();

    },[pageNumber])

    return (



        <Container >
            <Modal open={ modalOpen } close={ closeModal }>
                {modalContents}
            </Modal>
            <CategoryDirection tag1={'자유 거래 게시판'}></CategoryDirection>
            <br/>

            <Container >
                <Row>
                    <Col lg={{span:"9", offset:"1"}}>
                    <Table bordered hover size="md">
            <thead>
                <tr>
                <th>No</th>
                <th>Subject</th>

                <th>Read</th>
                <th>Date</th>
                </tr>
            </thead>
            <tbody>
            {posts}
            </tbody>

            </Table>

                    </Col>
                </Row>

            <BasicPagination history={history} path="/post"/>
            </Container>



        </Container>

    );
}
