import React, {useEffect, useState} from 'react';
import {useSelector, useDispatch} from 'react-redux'
import {setCategory} from '../modules/category'
import axios from "axios";
import Carousel from "react-bootstrap/Carousel";
import {Container, Navbar, Spinner} from 'react-bootstrap'
import Loading from './Loading'


export default function Home(props){
    const[products,Setproducts]= useState([])

    const dispatch = useDispatch()

    const fetchProducts= async ()=>{
        let res = await axios.get('/apis/v1/product');
        let product_list = res.data.payload.payload.filter(p => p.valid=== true);
        product_list = product_list.map(data=> {
                return  {
                    ...data,
                    id: data.pk
                }
            })
            Setproducts(product_list);
    }


    const fetchCategory= async ()=>{
        let res = await axios.get('/apis/v1/category');
        console.log(res);
        let category_list = res.data.payload.map(data=> {
                return{
                    kind: data.fields.kind,
                    pk: data.pk
                }
            });
        console.log('카테고리 리스트');
        console.log(category_list);
        dispatch(setCategory(category_list))

    }

    useEffect(()=>{
        fetchProducts();
        fetchCategory();
    },[])




        return (
            <div>

                <div>

                <ControlledCarousel/>



                <br/>
                <Container>
                <div style={{marginTop:30}}/>
                <h3>오늘의 상품 추천</h3>

                <hr/>
                <Loading products={products}/>

                </Container>



                </div>
            </div>
        );
}

function ControlledCarousel() {
    const [index, setIndex] = useState(0);

    const handleSelect = (selectedIndex, e) => {
        setIndex(selectedIndex);
    };
    return (

        <Carousel activeIndex={index} onSelect={handleSelect}>
            <Carousel.Item>
                <img
                    className="d-block w-100"
                    src="https://img.lovepik.com//back_pic/05/72/43/975bbd3c7e54c21.jpg_wh860.jpg"
                    alt="First slide"
                    style={{
                        minWidth: '100%',
                        height:'30rem'
                    }}
                />
                <Carousel.Caption>
                    {/* <h3>First slide label</h3>
                    <p>First slide label ~~~~</p> */}
                </Carousel.Caption>
            </Carousel.Item>
            <Carousel.Item>
                <img
                    className="d-block w-100"
                    src="https://image.freepik.com/free-vector/the-interior-of-mall-banner-scene-inside-a-shopping-store-at-night_93732-21.jpg"
                    alt="Second slide"
                    style={{
                        minWidth: '100%',
                        height:'30rem'
                    }}
                />

                <Carousel.Caption>
                    {/* <h3>Second slide label</h3>
                    <p>아무거나 적어 </p> */}
                </Carousel.Caption>
            </Carousel.Item>
            <Carousel.Item>
                <img
                    className="d-block w-100"
                    src="https://i.pinimg.com/originals/b9/42/dd/b942dd80ac8cba56dd90dc539d2040c0.jpg"
                    alt="Third slide"
                    style={{
                        minWidth: '100%',
                        height:'30rem'
                    }}
                />
                <Carousel.Caption>
                    {/* <h3>Third slide label</h3>
                    <p>
                    세번째
                    </p> */}
                </Carousel.Caption>
            </Carousel.Item>
        </Carousel>
    );
}
