import {loginAction} from '../modules/user'
import axios from 'axios'
import React, {useEffect, useState} from 'react'
import {useSelector, useDispatch} from 'react-redux'
import {Form, Container, Button, Row, Col, Table} from 'react-bootstrap'
import { CategoryDirection } from './CategoryBanner'
import '../App.css'
import Modal from './Modal'

export default function MainPost({history}){
    const [ modalOpen, setModalOpen ] = useState(false);
    const closeModal = () => {
        setModalOpen(false);
    }
    const [ modalContents, setModalContents ] = useState('');


    const dispatch = useDispatch();
    const [posts, setPosts] = useState([]);
    // const {username, password} = userData;

    const fetchPost = async()=>{
        let res = await axios.get('/apis/v1/post/page/1');
        let post_list = res.data.payload.map(item => {
            const {id, hit, title, createdAt} = item;

            return (

                <tr>
                <td>{id}</td>
                <td> {title}</td>
                <td>{createdAt}</td>
                <td>{hit}</td>
                <td>{createdAt}</td>
                </tr>

            )

        });

        setPosts(post_list);

    }


    useEffect(()=>{
        fetchPost();

    },[])


    // const onChangeHandler = (e) => {
    //     const {name, value} = e.target;
    //     setUserData({
    //         ...userData,
    //         [name]: value
    //     })

    // };
    // const onClickHandler = (e)=>{
    //     e.preventDefault();
    //     let body = {
    //         username: username,
    //         password: password
    //     };
    //     axios.post('/apis/v1/user/login', body)
    //     .then(response => {
    //         history.replace('/')
    //         dispatch(loginAction(response.data.payload))

    //     }).catch(e =>{
    //         setModalOpen(true);
    //         setModalContents('아이디와 패스워드를 확인해 주세요');

    //     })
    // }

    return (



        <Container >
            <Modal open={ modalOpen } close={ closeModal }>
                {modalContents}
            </Modal>
            <CategoryDirection tag1={'자유 거래 게시판'}></CategoryDirection>
            <br/>

            <Container size>
                <Table bordered hover size="md">
            <thead>
                <tr>
                <th>No</th>
                <th>Subject</th>
                <th>Name</th>
                <th>Read</th>
                <th>Date</th>
                </tr>
            </thead>
            <tbody>
            {posts}
            </tbody>

            </Table>

            </Container>



        </Container>

    );


    // return (<div style ={{
    //     display : 'flex', justifyContent : 'center', alignItems: 'center',
    //     width : '100%', height : '100vh'
    // }}>
    //     <form style ={{display : 'flex', flexDirection:'column'}}
    //         onSubmit={onClickHandler}>
    //         <label>username</label>
    //         <input name = "username" value = {username} onChange={onChangeHandler}/>
    //         <label>Password</label>
    //         <input name = "password" value = {password} onChange={onChangeHandler}/>
    //         <br/>
    //         <button type = 'submit'>
    //             Login
