import {loginAction} from '../modules/user'
import axios from 'axios'
import React, {useEffect, useState} from 'react'
import {useSelector, useDispatch} from 'react-redux'
import {Form, Container, Button, Row, Col, Table, ListGroup} from 'react-bootstrap'
import { CategoryDirection } from './CategoryBanner'
import BasicPagination from './BasicPagination'
import '../App.css'
import Modal from './Modal'
import Rating from './Rating'

export default function PostDetail({history, match}){
    let postId = match.params.number;
    const [ modalOpen, setModalOpen ] = useState(false);

    const closeModal = () => {
        setModalOpen(false);
    }

    const [modalContents, setModalContents] = useState('');
    const [user, setUser] = useState({username:"", temperature:"", celcius:""});
    const [post, setPost] = useState({title: "", description:"", area:"", hit:0, createdAt:"", Images:[]});
    const [comments, setComments] = useState([]);


    // const {username, password} = userData;

    const fetchPost = async()=>{
        await axios.get(`/apis/v1/post/${postId}/hit`);
        let res = await axios.get(`/apis/v1/post/${postId}`);
        let user_res = await axios.get(`/apis/v1/user/${res.data.payload.user_id}`);
        setUser(user_res.data.payload);
        setPost(res.data.payload);
    }

    const fetchComments = async()=>{
        let res = await axios.get(`/apis/v1/post/${postId}/comment`);
        setComments(res.data.payload);
    }


    useEffect(()=>{
        fetchPost();
        fetchComments();
    },[postId]);

    return (



        <Container >
            <Modal open={ modalOpen } close={ closeModal }>
                {modalContents}
            </Modal>
            <CategoryDirection tag1={'자유 거래 게시판'} tag2={"dddd"}></CategoryDirection>
            <br/>
            <div>
            <Row>
                <Col lg={{span:'10', offset:'1'}}>
                    <ListGroup>
                        <ListGroup.Item>{post.title}</ListGroup.Item>

                        <ListGroup.Item  style={{}}>
                        <Rating user={user} area={post.area}/>
                        </ListGroup.Item>

                        <ListGroup.Item>{post.description}</ListGroup.Item>
                        <ListGroup.Item>{post.area}</ListGroup.Item>
                        <ListGroup.Item>
                        <img style={{width: '50%',
                                height: 'auto'}} 
                            src={post.Images.length ? post.Images[0].image_url: ""}></img></ListGroup.Item>
                        <ListGroup.Item>Vestibulum at eros</ListGroup.Item>
                    </ListGroup>

                    <Form onSubmit={onClickHandler} >
                    <Form.Group as={Row} controlId="exampleForm.ControlInput1">
                        <Col sm={{ span: 2, offset: 2 }} 
                        lg={{ span: 1, offset: 3 }}>
                        <Form.Label style={{margin:10}}>아이디</Form.Label>

                        </Col>
                        <Col lg={{ span: 4, offset: 0 }}
                            sm={{ span: 8, offset: 0 }}>
                        <Form.Control 
                        size='lg'
                        name = 'username'
                        value = {username}
                        onChange={onChangeHandler}
                        placeholder="아이디를 입력해주세요" />
                        </Col>

                    </Form.Group>
                    <br/>
                    <Form.Group as={Row} controlId="exampleForm.ControlInput1">
                        <Col sm={{ span: 2, offset: 2 }} 
                        lg={{ span: 1, offset: 3 }}>
                        <Form.Label style={{margin:5}}>패스워드</Form.Label>

                        </Col>
                        <Col lg={{ span: 4, offset: 0 }}
                            sm={{ span: 8, offset: 0 }}>
                        <Form.Control
                        style={{fontFamily:'ubuntu'}}
                        size='lg'
                        type="password"
                        name = 'password'
                        // value = {password} 
                        // onChange={onChangeHandler}
                        placeholder="패스워드를 입력해주세요" />

                        </Col>
                        </Form.Group>
                    <br/>
                    <Row>
                        <Col lg = {{span:5, offset:3}}
                        sm = {{span:10, offset:2}}>
                        <div className="d-grid gap-2">
                        <button 
                        style={{height:50}}
                        type="submit"
                        className='filledButton'
                        >로그인</button>

                        </div>

                        </Col>
                    </Row>

                    <Row style={{marginTop:15}}>
                        <Col lg = {{span:5, offset:3}}
                        sm = {{span:10, offset:2}}>
                        <div className="d-grid gap-2">
                        <button 
                        style={{height:50}}
                        className='emptyButton'
                        onClick={(e) => {
                            e.preventDefault();
                            history.replace('/register');
                        }}
                        >회원가입

                        </button>

                        </div>

                        </Col>
                    </Row>
            </Form>

                </Col>
            </Row>


        </div>

        </Container>

    );
}
