import React, {useEffect, useState} from 'react';
import {useSelector, useDispatch} from 'react-redux'
import {setCategory} from '../../modules/category'
import axios from "axios";
import Carousel from "react-bootstrap/Carousel";
import {Container, Navbar, Spinner} from 'react-bootstrap'
import Loading from '../Loading'


export default function AuctionHome(props){
    const[products,Setproducts]= useState([])

    const dispatch = useDispatch()
    const fetchProducts= async ()=>{
        let res = await axios.get('/apis/v1/product');
        let product_list = res.data.payload.filter(p => p.valid=== true);
        product_list = product_list.map(data=> {
                return  {
                    ...data,
                    id: data.pk
                }
            })
            Setproducts(product_list);
    }


    const fetchCategory= async ()=>{
        let res = await axios.get('/apis/v1/category');

        let category_list = res.data.map(data=> {
                return{
                    kind: data.fields.kind,
                    pk: data.pk
                }
            });
        dispatch(setCategory(category_list))

    }

    useEffect(()=>{
        fetchProducts();
        fetchCategory();
    },[])




        return (
            <div>
                <div style={{ background: "#dedede" }}>
                <Container>
                    <Row>
                        <Col xs={{ span: 4, offset: 2 }}sm={{ span: 2, offset: 7 }}md={{ span: 1, offset: 9 }} lg={{ span: 1, offset: 8 }}>
                            <div
                                style={{ height: "2rem", fontSize: "1.2rem", margin: 3, color: "#44444" }}
                                onClick={() => {
                                    history.push("/auction/home");
                                }}
                            >
                                경매 홈
                            </div>
                        </Col>
                        <Col xs={{ span: 6, offset: 0 }}  sm={{ span: 3, offset: 0 }}  md={{ span: 2, offset: 0 }} lg={{ span: 2, offset: 0 }}>
                            <div
                                style={{ height: "2rem", fontSize: "1.2rem", margin: 3, color: "#44444" }}
                                onClick={() => {
                                    history.push("/auction/register");
                                }}
                            >
                                경매상품 등록하기
                            </div>
                        </Col>
                    </Row>
                </Container>
            </div>

                <div>


                <br/>
                <Container>
                <div style={{marginTop:30}}/>

                <h3 style={{marginTop:40}}>오늘의 경매</h3>

                <hr/>
                <Loading products={products}/>

                </Container>



                </div>
            </div>
        );
}
