import {loginAction} from '../modules/user'
import axios from 'axios'
import React, {useState} from 'react'
import {useSelector, useDispatch} from 'react-redux'
import {Form, Container, Button, Row, Col} from 'react-bootstrap'
import { CategoryDirection } from './CategoryBanner'


function Login({history}){
    const dispatch = useDispatch();
    const [userData, setUserData] = useState({
        username: "",
        password: ""
    })
    const {username, password} = userData;

    const onChangeHandler = (e) => {
        const {name, value} = e.target;
        setUserData({
            ...userData,
            [name]: value
        })

    };
    const onClickHandler = (e)=>{
        e.preventDefault();
        let body = {
            username: username,
            password: password
        };
        axios.post('/apis/v1/user/login', body)
        .then(response => {
            history.replace('/')
            console.log("login")
            dispatch(loginAction(response.data.payload.payload))
            alert("환영합니다!")
        }).catch(e =>{
            alert("로그인 실패")
            console.log("로그인 실패")
        })
    }

    return (



        <Container >
            <CategoryDirection tag1={'로그인'}></CategoryDirection>
            <br/>
            <div style={{fontSize:'1.3rem'}}>


            <Form onSubmit={onClickHandler} >
            <Form.Group as={Row} controlId="exampleForm.ControlInput1">
                <Col sm={{ span: 2, offset: 2 }} 
                lg={{ span: 1, offset: 3 }}>
                <Form.Label style={{margin:10}}>아이디</Form.Label>

                </Col>
                <Col lg={{ span: 4, offset: 0 }}
                    sm={{ span: 8, offset: 0 }}>
                <Form.Control 
                size='lg'
                name = 'username'
                value = {username}
                onChange={onChangeHandler}
                placeholder="아이디를 입력해주세요" />
                </Col>

            </Form.Group>
            <br/>
            <Form.Group as={Row} controlId="exampleForm.ControlInput1">
                <Col sm={{ span: 2, offset: 2 }} 
                lg={{ span: 1, offset: 3 }}>
                <Form.Label style={{margin:5}}>패스워드</Form.Label>

                </Col>
                <Col lg={{ span: 4, offset: 0 }}
                    sm={{ span: 8, offset: 0 }}>
                <Form.Control
                size='lg'
                type="password"
                name = 'password'
                value = {password} 
                onChange={onChangeHandler}
                placeholder="패스워드를 입력해주세요" />

                </Col>
                </Form.Group>
            <br/>
            <Row>
                <Col lg = {{span:6, offset:2}}
                sm = {{span:12, offset:0}}>
                <div className="d-grid gap-2">
                <Button type="submit"

                size='lg'
                variant="outline-light" 
                style={{background: '#e85255', 
                fontSize:'1.3rem',
                marginLeft: 100
                }}
                >Login</Button>

                </div>

                </Col>
            </Row>

            <Row>
                <Col lg = {{span:6, offset:2}}
                sm = {{span:12, offset:0}}>
                <div className="d-grid gap-2">
                <Button type="submit"

                size='lg'
                variant="outline-light" 
                style={{background: '#e85255', 
                fontSize:'1.3rem',
                marginLeft: 100
                }}
                >Login</Button>

                </div>

                </Col>
            </Row>
            </Form>
            </div>
        </Container>

    );


    // return (<div style ={{
    //     display : 'flex', justifyContent : 'center', alignItems: 'center',
    //     width : '100%', height : '100vh'
    // }}>
    //     <form style ={{display : 'flex', flexDirection:'column'}}
    //         onSubmit={onClickHandler}>
    //         <label>username</label>
    //         <input name = "username" value = {username} onChange={onChangeHandler}/>
    //         <label>Password</label>
    //         <input name = "password" value = {password} onChange={onChangeHandler}/>
    //         <br/>
    //         <button type = 'submit'>
    //             Login
    //         </button>
    //     </form>
    // </div>);
}

export default Login;
