import React, {useState, useEffect} from 'react'
import axios from 'axios'
import {ListGroup, Container} from 'react-bootstrap'
import Title from './Title'
import {useSelector} from 'react-redux'
function OrderList({history}){
    const {isLoggedIn, userData} = useSelector(state =>({
        isLoggedIn: state.user.isLoggedIn,
        userData: state.user.payload
    }))
    const [products, setProducts] = useState([{}])

    const [orders, setOrders] = useState([]) 
    const fetchOrders= ()=>{

        axios.get('/apis/v1/order/' + userData.user_id).then(res=> {
            let order_list = res.data.payload.map((data, index)=> {
                console.log(products)
                console.log("hih")
                console.log(products)
            let product = products.find(pro => pro.product_id == data.fields.product_id)

            console.log(product)
                return ({
                    ...product,
                    ...data.fields
                })
            })
            setOrders(order_list);
            console.log("here")
            console.log(order_list)
        })
    }
    const fetchProducts= ()=>{
         axios.get('/apis/v1/product').then(res=> {
            console.log("res")
            console.log(res.data.payload)
            let tmp = res.data.payload.map(data=> {
                return  {
                    ...data.fields,
                    product_id: data.pk
                }
            })
            console.log("product할당")
            console.log(tmp)
            setProducts(tmp);

            console.log(products)
            fetchOrders()
        })
    }


    useEffect(()=>{
        fetchProducts()


    },[userData.user_id])

    console.log(orders)
    if(isLoggedIn === false){
        alert('로그인 후 이용해주세요!')
        history.replace('/login')
    }

    return (<div>
        <Title title="구매 목록" set_middle={false}></Title>
        <Container>
        <ListGroup>
            {/* {orders} */}
        </ListGroup>
        </Container>


    </div>)
}

export default OrderList
