import React, {useState, useEffect} from 'react'
import axios from 'axios'
import {ListGroup, Container} from 'react-bootstrap'
import Title from './Title'
import {useSelector} from 'react-redux'
function OrderList({history}){
    const {isLoggedIn, userData} = useSelector(state =>({
        isLoggedIn: state.user.isLoggedIn,
        userData: state.user.payload
    }))
    const [products, setProducts] = useState([])

    const [orders, setOrders] = useState([]) 
    const fetchOrders= async ()=>{

        await axios.get('/apis/v1/order/' + userData.user_id).then(res=> {
            let order_list = res.data.payload.map((data, index)=> {
            let product = products.find(pro => pro.product_id === data.fields.product_id)
                return (
                    data.fields
                )
            })

            setOrders(order_list);
            console.log(product)
        })
    }
    const fetchProducts= async ()=>{
        await axios.get('/apis/v1/product').then(res=> {
            console.log(res)
            let tmp = res.data.payload.map(data=> {

                return  {
                    ...data.fields,
                    product_id: data.pk
                }
            })
            setProducts(tmp);
            console.log(products)
        })
    }


    useEffect(()=>{
        fetchProducts()
        fetchOrders()

    },[userData.user_id])

    if(isLoggedIn === false){
        alert('로그인 후 이용해주세요!')
        history.replace('/login')
    }

    return (<div>
        <Title title="구매 목록" set_middle={false}></Title>
        <Container>
        <ListGroup>
            {/* {orders} */}
        </ListGroup>
        </Container>


    </div>)
}

export default OrderList
