import { ListGroup, Container, Button, Form, Row, Col, ListGroupItem } from "react-bootstrap";
import axios from "axios";
import { useSelector } from "react-redux";
import { Link, useHistory } from "react-router-dom";
import EmptyHeartImg from "../../assets/images/heart.png";
import HeartImg from "../../assets/images/heart_pressed.png";
import { CategoryDirection } from "../CategoryBanner";
import { setMoney, setDate } from "../Convenient";
import Rating from "../Rating";
import Modal from "../Modal";
import Socket from "../network/socket";
import React, { memo, useState, useEffect } from "react";
import parseDate from "../util/date";
import ProductService from "../service/product";

const Products = memo(({ product, productService, onError, oneproduct }) => {
    const history = useHistory();
    const {
        id,
        buyer_id,
        // seller_id,
        createdAt,
        fileurl,
        finish,
        name,
        price,
        description,
    } = product;

    const path = "/" + id;
    const gotoProductadd = (product) => {
        history.push({
            pathname: "/productadd",
            state: { products: product },
        });
    };

    const onClick = (event) => {
        productService
            .getProducts(id) // id에 맞는 상품 들고오기
            .then((product) => gotoProductadd(product))
            .catch(onError);
    };
    const finished = { id: id, finish: true };
    const onfinish = () => {
        productService
            .updateProduct(finished)
            .then((pro) => productService.postBuyer(pro))
            .catch(onError);
    };
    const refresh = () => {
        window.location.replace("/");
    };

    const Plus = async () => {
        productService.plusProduct(id, buyer_id); // 입찰시 금액 10% 추가
    };
    const remove = async () => {
        console.log("타입은?", typeof id);
        productService.removeProduct(id); // id 찾아서 그 상품 삭제
        setTimeout(refresh, 20000);
    };

    return (
        <>
            <section className="card">
                <Link to={path}>
                    {fileurl.map((url) => {
                        return <img key={Math.random()} className="card-img-top" src={url.fileurls} alt="Card image cap" />;
                    })}
                    <div className="card-body">
                        <h5 className="card-title">{name}</h5>

                        {(oneproduct && (
                            <>
                                <span className="card-text">{price}</span>
                                <h3> - buyer_id : {(buyer_id && buyer_id) || "현재 입찰자 없음"}</h3>
                                <button onClick={Plus}>{parseInt(price * 1.1)}원에 입찰하기</button>
                            </>
                        )) || <span>입찰은 상품 상세에서 가능합니다. 현재 금액을 보시거나 입찰하시려면 상품을 클릭하세요</span>}

                        <p className="card-text">
                            <small className="text-muted">{description}</small>
                        </p>
                        <span>{finish == false && parseDate(createdAt).length == 27 ? onfinish() : parseDate(createdAt)}</span>
                    </div>
                </Link>
            </section>
            {finish == false ? (
                <>
                    <button onClick={remove}>삭제하기</button>

                    <button onClick={onClick}>상품 수정하기</button>
                </>
            ) : (
                ""
            )}

            <hr />
        </>
    );
});

const AuctionDetail = ({ match }) => {
    const baseURL = process.env.REACT_APP_BASE_URL;
    const socketClient = new Socket(baseURL);
    const productService = new ProductService(socketClient);
    const product_id = match.params.number;
    const history = useHistory();
    console.log("히스토리", history);
    const [products, setProducts] = useState([]);
    const [error, setError] = useState("");
    useEffect(() => {
      product_id
        ? productService
            .getProducts(product_id) // product_id인 상품 가져오기
            .then(product => setProducts([{ ...product }]))
            .catch(onError)
        : productService
            .getProducts() // 모든 상품 가져오기
            .then(product => setProducts([...product]))
            .catch(onError);
      const stopSync = productService.onSync(product => onCreated(product));
      return () => stopSync();
    }, [productService]);

    const onCreated = (product) => {
        setProducts((products) => (products[0].id == product.id && products.length == 1 ? [product] : products));
        // setProducts(products => (products.length == 1 ? [product] : products));

        // setProducts(products) : setProducts(product);
    };
    console.log("이건", products);

    const onError = (error) => {
        setError(error.toString());
        setTimeout(() => {
            setError("");
        }, 3000);
    };

    return (
        <>
            {products.length !== 1 ? "" : (
                <ul>
                    {products.map((product) => (
                        <Products oneproduct={true} productService={productService} key={product.id} product={product} onError={onError} />
                    ))}
                </ul>
            )}
        </>
    );
};

export default AuctionDetail;
