import {loginAction} from '../modules/user'
import axios from 'axios'
import React, {useState} from 'react'
import {useSelector, useDispatch} from 'react-redux'
import {Form, Container, Button} from 'react-bootstrap'
import Banner from './Banner'


function Login(){
    const dispatch = useDispatch();
    const [userData, setUserData] = useState({
        username: "",
        password: ""
    })
    const {username, password} = userData;

    const onChangeHandler = (e) => {
        const {name, value} = e.target;
        setUserData({
            ...userData,
            [name]: value
        })

    };
    const onClickHandler = (e)=>{
        e.preventDefault();
        let body = {
            username: username,
            password: password
        };
        axios.post('/apis/v1/user/login', body)
        .then(response => {
            // props.history.push('/')
            console.log("로그인 성공")
            dispatch(loginAction(response.data.payload))
            alert("로그인 성공")
        }).catch(e =>{
            alert("로그인 실패")
            console.log("로그인 실패")
        })

        setUserData({
            username: "",
            password: ""
        })
    }

    return (
        <div>
        <Banner/>
        <Container>
            <Form onSubmit={onClickHandler} >


            <Form.Group controlId="exampleForm.ControlInput1">
                <Form.Label>상품명</Form.Label>
                <Form.Control 
                name = 'name'
                value = {name}
                onChange={onChangeHandler}
                placeholder="상품명을 적어주세요" />
            </Form.Group>

            <Form.Group controlId="exampleForm.ControlInput1">
                <Form.Label>가격</Form.Label>
                <Form.Control
                name = 'price'
                value = {price} 
                onChange={onChangeHandler}
                placeholder="가격을 적어주세요" />
            </Form.Group>

            <Form.Group controlId="exampleForm.ControlInput1">
                <Form.Label>수량</Form.Label>
                <Form.Control
                name = 'quantity'
                value = {quantity} 
                onChange={onChangeHandler}
                placeholder="수량을 적어주세요" />
            </Form.Group>
{/* 
            <Form.Group controlId="exampleForm.ControlInput1">
                <Form.Label>사진</Form.Label>
                <Form.Control placeholder="name@example.com" />
            </Form.Group> */}



            <Form.Group controlId="exampleForm.ControlTextarea1">
                <Form.Label>상품 설명</Form.Label>
                <Form.Control 
                as="textarea" 
                rows={5}
                onChange={onChangeHandler}
                name = 'description'
                value = {description}
                />
            </Form.Group>
            <Button type="submit">등록</Button>
            </Form>
        </Container>
        </div>
    );


    // return (<div style ={{
    //     display : 'flex', justifyContent : 'center', alignItems: 'center',
    //     width : '100%', height : '100vh'
    // }}>
    //     <form style ={{display : 'flex', flexDirection:'column'}}
    //         onSubmit={onClickHandler}>
    //         <label>username</label>
    //         <input name = "username" value = {username} onChange={onChangeHandler}/>
    //         <label>Password</label>
    //         <input name = "password" value = {password} onChange={onChangeHandler}/>
    //         <br/>
    //         <button type = 'submit'>
    //             Login
    //         </button>
    //     </form>
    // </div>);
}

export default Login;
