import React, {useEffect, useState} from 'react'
import { useSelector, useDispatch } from 'react-redux'
import {setUserAction, loginAction} from '../modules/user'
import axios from 'axios'


function Login(){
    const dispatch = useDispatch();
    const [userData, setUserData] = useState({
        username: "",
        password: ""
    })
    const {username, password} = userData;

    const onChangeHandler = (e) => {
        const {name, value} = e.target;
        setUserData({
            ...userData,
            [name]: value
        })

    };
    const onClickHandler = (e)=>{
        let body = {
            username: username,
            password: password
        };
        dispatch(loginAction(body)).then(
            response=>{
                console.log(response)
            }
        );

        setUserData({
            username: "",
            password: ""
        })
    }


    return (<div>
        <p>아이디</p>
        <input name='username' value={username} onChange={onChangeHandler} placeholder="아이디를 입력해주세요"></input>
        <p>비밀번호</p>
        <input name='password' value={password} onChange={onChangeHandler} placeholder="비밀번호를 입력해주세요"></input>
        <br/>
        <button onClick={onClickHandler}>로그인</button>


    </div>);
}

export default Login;
