import React, {useEffect, useState} from 'react';
import {useSelector, useDispatch} from 'react-redux'
import {setCategory} from '../modules/category'
import axios from "axios";
import Carousel from "react-bootstrap/Carousel";
import {Container, Navbar} from 'react-bootstrap'
import DisplayProducts from './DisplayProduct'




export default function Home(props){
    const[products,Setproducts]= useState([])

    const dispatch = useDispatch()

    const fetchProducts= async ()=>{
        await axios.get('/apis/v1/product').then(res=> {
            console.log(res)
            let product_list = res.data.payload.payload.map(data=> {

                return  {
                    ...data,
                    id: data.pk
                }
            })
            Setproducts(product_list);

        })
    }
    const fetchCategory= async ()=>{
        await axios.get('/apis/v1/category').then(res=> {
            let category_list = res.data.payload.map(data=> {
                return data.fields
            })

            console.log(category_list);
            dispatch(setCategory(category_list))
        }).catch((e) => {
            console.log(e)
        } )
    }

    useEffect(()=>{
        fetchProducts();
        fetchCategory();
    },[])
        return (
            <div>
                <br/>
                <div style={{marginTop:30}}>
                <Container>

                <ControlledCarousel/>

                </Container>

                <br/>
                <Container>
                <div style={{marginTop:30}}/>
                <h3>오늘의 상품 추천</h3>
                <hr/>
                <div style={{marginTop:30}}/>
                    <DisplayProducts products={products}/>
                </Container>



                </div>
            </div>
        );
}

function ControlledCarousel() {
    const [index, setIndex] = useState(0);

    const handleSelect = (selectedIndex, e) => {
        setIndex(selectedIndex);
    };
    return (
        <div>
            <Carousel activeIndex={index} onSelect={handleSelect}>
      <Carousel.Item>
        <img
          className="d-block w-100"
          src="holder.js/800x400?text=First slide&bg=373940"
          alt="First slide"
        />
        <Carousel.Caption>
          <h3>First slide label</h3>
          <p>Nulla vitae elit libero, a pharetra augue mollis interdum.</p>
        </Carousel.Caption>
      </Carousel.Item>
      <Carousel.Item>
        <img
          className="d-block w-100"
          src="holder.js/800x400?text=Second slide&bg=282c34"
          alt="Second slide"
        />

        <Carousel.Caption>
          <h3>Second slide label</h3>
          <p>Lorem ipsum dolor sit amet, consectetur adipiscing elit.</p>
        </Carousel.Caption>
      </Carousel.Item>
      <Carousel.Item>
        <img
          className="d-block w-100"
          src="holder.js/800x400?text=Third slide&bg=20232a"
          alt="Third slide"
        />

        <Carousel.Caption>
          <h3>Third slide label</h3>
          <p>
            Praesent commodo cursus magna, vel scelerisque nisl consectetur.
          </p>
        </Carousel.Caption>
      </Carousel.Item>
    </Carousel>




        </div>

    );
}
