import {ListGroup, Container, Button, Form, Row, Col} from 'react-bootstrap'
import axios from 'axios'
import {useEffect, useState} from 'react'
import {useSelector} from 'react-redux'
import Title from './Title'

function PurchasePage({match}){
    const {product, demand_amount} = match.location.state 
    const {isLoggedIn, userData} = useSelector(state =>({
        isLoggedIn: state.user.isLoggedIn,
        userData: state.user.payload
    }))
    const [purchaseData, setPurchaseData] = useState({
        address: null,
        email_address: null
    })
    const {address, email_address} = purchaseData

    const orderProduct= async ()=>{
        let body = {
            ...product,
            seller_id: product.seller_id,
            buyer_id: userData.buyer_id,
            demand_amount: demand_amount,
        };
        console.log(body)
        await axios.post('/apis/v1/order/', body).then(res=> {
            console.log(res.data.payload.payload)
            alert('주문 성공')
        })
        .catch(e => {
            // 정보가 없을 때 처리
            alert('주문 실패')
        })
    }
    // useEffect(()=>{

    //         fetchProduct()

    // },[match.params.number])

    const onClickOrder = () => {
        if(isLoggedIn === false){
            alert("로그인 후 이용하세요.")
            return;
        }


        orderProduct();
    }

    const onChangeHandler = (e) => {
        const {value} = e.target
        if(value < 1){
            alert("올바르지 않습니다.")
            return;
        }

        setAmount(parseInt(value))
    }

    return(
        <div>
        <Title title= {product.name}></Title>
        <Container>
            <Row className="justify-content-md-center">
                <Col xs lg="8">
                <ListGroup>
            <ListGroup.Item>{product.category}</ListGroup.Item>
            <ListGroup.Item>상품명 / {product.name}</ListGroup.Item>
            <ListGroup.Item>상품 가격 / {product.price}</ListGroup.Item>
            <ListGroup.Item>상품 수량 / {product.quantity}</ListGroup.Item>
            <ListGroup.Item>{product.description}</ListGroup.Item>
            <ListGroup.Item>{product.created_at}</ListGroup.Item>
            </ListGroup>

        <br/>
        <p>구매 수량</p>
        <input type='number' onChange={onChangeHandler}value={amount}></input>
        <br/>
        <br/>
        <Form>
            <Form.Group controlId="exampleForm.ControlInput1">
                <Form.Label>Email address</Form.Label>
                <Form.Control 
                name = 'email_address'
                value = {email_address}
                onChange={onChangeHandler}
                placeholder="배송 정보를 받을 이메일을 적어주세요" />
            </Form.Group>

            <Form.Group controlId="exampleForm.ControlInput1">
                <Form.Label>Address</Form.Label>
                <Form.Control
                name = 'address'
                value = {address} 
                onChange={onChangeHandler}
                placeholder="주소를 적어주세요" />
            </Form.Group>


            </Form>

        <Button onClick={onClickOrder}>구매하기</Button>
                </Col>
            </Row>

        </Container>

        </div>

    )
}

export default PurchasePage
